mapper.select:
SELECT SEQ, USERID, CONTENT, REGDATE,
(SELECT NICKNAME FROM SC_USER SU WHERE SU.ID = SC_COMMENT.USERID) NICKNAME		 
FROM SC_COMMENT

		WHERE BBSSEQ = #{bbsseq}


		WHERE PDSSEQ = #{pdsseq}


		WHERE REVIEWSEQ = #{reviewseq}


		WHERE NOTICESEQ = #{noticeseq}

ORDER BY REGDATE DESC

mapper.update:
UPDATE SC_COMMENT
SET CONTENT=#{content}
WHERE SEQ=#{seq}

mapper.delete:
DELETE FROM SC_COMMENT

	WHERE BBSSEQ = #{bbsseq}


	WHERE PDSSEQ = #{pdsseq}


	WHERE REVIEWSEQ = #{reviewseq}


	WHERE NOTICESEQ = #{noticeseq}
